# 싱글톤 패턴을 자바스크립트로

```javascript
class Singleton {
  constructor() {
    // 싱글톤 인스턴스가 아직 한번도 안 만들어졌을 때
    if (!Singleton.instance) {
      Singleton.instance = this;
    }
    // 이미 만들어져 있다면 퍼가요
    return Singleton.instance;
  }
  getInstance() {
    return this;
  }
}

const 첫째 = new Singleton();
const 둘째 = new Singleton();

console.log(첫째 === 둘째);
```

